-- ----------------------------
-- Table structure for techcrunch
-- ----------------------------
DROP TABLE IF EXISTS `techcrunch`;
CREATE TABLE `techcrunch` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(255) NOT NULL,
  `author` varchar(50) DEFAULT NULL,
  `content` varchar(10000) DEFAULT NULL,
  `picUrl` varchar(120) DEFAULT NULL,
  `postDate` varchar(30) DEFAULT NULL,
  `writeDate` date DEFAULT NULL,
  `source` varchar(300) DEFAULT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `title` (`title`)
) ENGINE=InnoDB AUTO_INCREMENT=19 DEFAULT CHARSET=utf8;

